mapper.select:
select *
from TB_ACTIVITY_PROFIT a
where if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'
AND ACTIVITY_COUPON_TYPE='2'
and (if(length(a.APPLY_PRODUCT_FLAG),a.APPLY_PRODUCT_FLAG,'0') = '1' or
EXISTS (select 1 from TB_ACTIVITY_REF_PRODUCT b where
b.ACTIVITY_ID=a.UUID and b.PRODUCT_ID=#{productId,jdbcType=VARCHAR}

    and b.SKU_ID=#{skuId,jdbcType=VARCHAR}

)
)
Order by a.CREATE_TIME desc

select a.* from TB_ACTIVITY_PROFIT a left join TB_ACTIVITY_QUOTA_RULE b on a.UUID = b.ACTIVITY_ID and if(length(b.IS_ENABLE),b.IS_ENABLE,'1') = '1' where if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1' AND a.STATUS='1' and a.ALLOW_USE_BEGIN_DATE <= sysdate() and a.ALLOW_USE_END_DATE >= sysdate() AND ACTIVITY_COUPON_TYPE='2'
and (if(length(a.APPLY_PRODUCT_FLAG),a.APPLY_PRODUCT_FLAG,'0') = '1' or EXISTS (select 1 from TB_ACTIVITY_REF_PRODUCT b where b.ACTIVITY_ID=a.UUID and b.PRODUCT_ID=#{productId,jdbcType=VARCHAR}
and b.SKU_ID=#{skuId,jdbcType=VARCHAR}
) )
and (ifnull(b.PER_DATE_MAX_AMOUNT,999999999)>=999999999 or b.PER_DATE_MAX_AMOUNT > (select ifnull(sum(ifnull(c.Profit_Value, 0)),0) from TB_CLIENT_TAKE_IN_ACTIVITY c where BUSIN_CODE = '5003' and if(length(c.IS_ENABLE),c.IS_ENABLE,'1') = '1' and c.ACTIVITY_ID = a.UUID and c.create_time >= curdate())) and (ifnull(b.Max_Amount,999999999)>=999999999 or b.Max_Amount > (select ifnull(sum(ifnull(c.Profit_Value, 0)),0) from TB_CLIENT_TAKE_IN_ACTIVITY c where BUSIN_CODE = '5003' and if(length(c.IS_ENABLE),c.IS_ENABLE,'1') = '1' and c.ACTIVITY_ID = a.UUID)) and (ifnull(b.Per_Date_Max_Count,999999999)>=999999999 or b.Per_Date_Max_Count > (select ifnull(sum(ifnull(c.Product_Count, 0)),0) from TB_CLIENT_TAKE_IN_ACTIVITY c where BUSIN_CODE = '5003' and if(length(c.IS_ENABLE),c.IS_ENABLE,'1') = '1' and c.ACTIVITY_ID = a.UUID and c.create_time >= curdate())) and (ifnull(b.Max_Count,999999999)>=999999999 or b.Max_Count > (select ifnull(sum(ifnull(c.Product_Count, 0)),0) from TB_CLIENT_TAKE_IN_ACTIVITY c where BUSIN_CODE = '5003' and if(length(c.IS_ENABLE),c.IS_ENABLE,'1') = '1' and c.ACTIVITY_ID = a.UUID)) Order by a.CREATE_TIME desc

select b.PRODUCT_ID as productId,b.SKU_ID as skuId,a.PROFIT_VALUE as price from TB_ACTIVITY_PROFIT a INNER join TB_ACTIVITY_REF_PRODUCT b on b.ACTIVITY_ID=a.UUID and if(length(b.IS_ENABLE),b.IS_ENABLE,'1') = '1' where if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1' AND a.STATUS='1' and a.ALLOW_USE_BEGIN_DATE <= sysdate() and a.ALLOW_USE_END_DATE >= sysdate() AND ACTIVITY_COUPON_TYPE='2' and (if(length(a.APPLY_PRODUCT_FLAG),a.APPLY_PRODUCT_FLAG,'0') = '1' or ( b.PRODUCT_ID=#{productId,jdbcType=VARCHAR}
and b.SKU_ID=#{skuId,jdbcType=VARCHAR}
) ) Order by a.CREATE_TIME desc

select * from TB_ACTIVITY_PROFIT a where if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'
AND a.STATUS=#{status,jdbcType=VARCHAR}
and a.ALLOW_USE_BEGIN_DATE >sysdate()
and a.ALLOW_USE_BEGIN_DATE <=sysdate() and a.ALLOW_USE_END_DATE >=sysdate()
and a.ALLOW_USE_END_DATE <sysdate()
and a.ALLOW_USE_END_DATE >sysdate()
and (if(length(a.APPLY_PRODUCT_FLAG),a.APPLY_PRODUCT_FLAG,'0') = '1' or EXISTS (select 1 from TB_ACTIVITY_REF_PRODUCT b where b.ACTIVITY_ID=a.UUID and b.PRODUCT_ID=#{productId,jdbcType=VARCHAR}
and b.SKU_ID=#{skuId,jdbcType=VARCHAR}
)) Order by a.CREATE_TIME desc

mapper.update:
UPDATE TB_ACTIVITY_PROFIT a
SET a.IS_ENABLE='0'

    ,a.UPDATE_AUTHOR=#{operator,jdbcType=VARCHAR}

where if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'
AND a.UUID IN

    #{baseActivity.activityId}

mapper.select:
SELECT * FROM TB_ACTIVITY_PROFIT a
where if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'

    AND if(length(a.STATUS),a.STATUS,'1')=#{commonQry.upAndDownStatus,jdbcType=VARCHAR}


    and a.UUID=#{commonQry.activityId,jdbcType=VARCHAR}


    and a.ALLOW_USE_BEGIN_DATE >sysdate()


    and a.ALLOW_USE_BEGIN_DATE <=sysdate()
    and a.ALLOW_USE_END_DATE >=sysdate()


    and a.ALLOW_USE_END_DATE <sysdate()


    and a.ALLOW_USE_END_DATE >sysdate()


    and a.ALLOW_USE_BEGIN_DATE >sysdate()


    and instr(#{commonQry.activityCouponType,jdbcType=VARCHAR},a.ACTIVITY_COUPON_TYPE) >0


    and a.ACTIVITY_NAME like CONCAT('%',#{commonQry.activityName,jdbcType=VARCHAR},'%')


    and( a.APPLY_PRODUCT_FLAG='1' or
    exists(select 1 from TB_ACTIVITY_REF_PRODUCT b where b.ACTIVITY_ID = a.UUID
    and b.PRODUCT_ID =#{commonQry.productId,jdbcType=VARCHAR}

        and b.SKU_ID =#{commonQry.skuId,jdbcType=VARCHAR}

    )
    )

Order by a.CREATE_TIME desc

SELECT a.ACTIVITY_COUPON_TYPE as gatherItem,
count(1) as gatherValue
FROM TB_ACTIVITY_PROFIT a
where if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'

    AND if(length(a.STATUS),a.STATUS,'1')=#{upAndDownStatus,jdbcType=VARCHAR}


    and a.UUID=#{activityId,jdbcType=VARCHAR}


    and a.ALLOW_USE_BEGIN_DATE >sysdate()


    and a.ALLOW_USE_BEGIN_DATE <=sysdate()
    and a.ALLOW_USE_END_DATE >=sysdate()


    and a.ALLOW_USE_END_DATE <sysdate()


    and instr(#{activityCouponType,jdbcType=VARCHAR},a.ACTIVITY_COUPON_TYPE) >0


    and a.ACTIVITY_NAME like CONCAT('%',#{activityName,jdbcType=VARCHAR},'%')


    and( a.APPLY_PRODUCT_FLAG='1' or
    exists(select 1 from TB_ACTIVITY_REF_PRODUCT b where b.ACTIVITY_ID = a.UUID
    and b.PRODUCT_ID =#{productId,jdbcType=VARCHAR}

        and b.SKU_ID =#{skuId,jdbcType=VARCHAR}

    )
    )

group by ACTIVITY_COUPON_TYPE

select CONCAT(PRODUCT_ID,'|',if(length(SKU_ID),SKU_ID,'EMPTY')) as gatherItem, count(*) as gatherValue from TB_ACTIVITY_PROFIT a inner join TB_ACTIVITY_REF_PRODUCT b on b.ACTIVITY_ID=a.UUID and if(length(b.IS_ENABLE),b.IS_ENABLE,'1') = '1' where if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1' AND a.STATUS='1' and a.ALLOW_USE_BEGIN_DATE <= sysdate() and a.ALLOW_USE_END_DATE >= sysdate() and if(length(a.APPLY_PRODUCT_FLAG),a.APPLY_PRODUCT_FLAG,'0') = '0' and PRODUCT_ID IN
#{product.productId}
group by CONCAT(PRODUCT_ID,'|',if(length(SKU_ID),SKU_ID,'EMPTY'))

SELECT * FROM TB_ACTIVITY_PROFIT a where if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'
and (a.UUID=#{activityId,jdbcType=VARCHAR} or a.ACTIVITY_NAME like CONCAT('%',#{activityName,jdbcType=VARCHAR},'%') or ( a.APPLY_PRODUCT_FLAG='1' or exists( select 1 from TB_ACTIVITY_REF_PRODUCT b where b.ACTIVITY_ID = a.UUID and b.PRODUCT_ID =#{productId,jdbcType=VARCHAR}
and b.SKU_ID =#{skuId,jdbcType=VARCHAR}
) ) )
and a.ALLOW_USE_BEGIN_DATE >sysdate()
and a.ALLOW_USE_BEGIN_DATE <=sysdate() and a.ALLOW_USE_END_DATE >=sysdate()
and a.ALLOW_USE_END_DATE <sysdate()
and instr(#{activityCouponType,jdbcType=VARCHAR},a.ACTIVITY_COUPON_TYPE) >0
Order by a.CREATE_TIME desc

select * from TB_ACTIVITY_PROFIT a where if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1' AND a.ACTIVITY_COUPON_TYPE='2'
AND a.STATUS=#{status,jdbcType=VARCHAR}
and a.ALLOW_USE_BEGIN_DATE >sysdate()
and a.ALLOW_USE_BEGIN_DATE <=sysdate() and a.ALLOW_USE_END_DATE >=sysdate()
and a.ALLOW_USE_END_DATE <sysdate()
and a.ALLOW_USE_END_DATE >sysdate()
and EXISTS (select 1 from TB_ACTIVITY_REF_PRODUCT b where b.ACTIVITY_ID=a.UUID and if(length(b.IS_ENABLE),b.IS_ENABLE,'1') = '1' and b.PRODUCT_ID in
#{idstr}
) Order by a.CREATE_TIME desc

select *
from TB_ACTIVITY_PROFIT a
where if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'
AND ACTIVITY_COUPON_TYPE='2'

    AND a.UUID !=#{activityId,jdbcType=VARCHAR}

and (if(length(a.APPLY_PRODUCT_FLAG),a.APPLY_PRODUCT_FLAG,'0') = '1' or
EXISTS (select 1 from TB_ACTIVITY_REF_PRODUCT b
where b.ACTIVITY_ID=a.UUID and b.PRODUCT_ID=#{productId,jdbcType=VARCHAR}
and b.SKU_ID is null
)
)
Order by a.CREATE_TIME desc
