SELECT * FROM TB_ACTIVITY_REF_PRODUCT a
WHERE IFNULL(a.IS_ENABLE, '1') = '1'
AND a.ACTIVITY_ID=#{activityId,jdbcType=VARCHAR}
and a.PRODUCT_ID=#{productId,jdbcType=VARCHAR}
and a.SKU_ID is null

