mapper.select:
SELECT * FROM TB_ACTIVITY_REF_PRODUCT a
WHERE if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'
AND a.ACTIVITY_ID=#{activityId,jdbcType=VARCHAR}
and a.PRODUCT_ID=#{productId,jdbcType=VARCHAR}
and a.SKU_ID is null

SELECT * FROM TB_ACTIVITY_REF_PRODUCT a
WHERE if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'
AND a.ACTIVITY_ID=#{activityId,jdbcType=VARCHAR}
and a.PRODUCT_ID=#{productId,jdbcType=VARCHAR}
and (a.SKU_ID=#{skuId,jdbcType=VARCHAR} or if(length(a.SKU_ID),a.SKU_ID,'EMPTY')='EMPTY')

SELECT CONCAT(PRODUCT_ID,'|',if(length(a.SKU_ID),a.SKU_ID,'EMPTY')) as gatherItem, 1 as gatherValue FROM TB_ACTIVITY_REF_PRODUCT a WHERE if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'
AND a.ACTIVITY_ID!=#{activity.uuid,jdbcType=VARCHAR}
and exists( select 1 from TB_ACTIVITY_PROFIT b where b.UUID=a.ACTIVITY_ID and if(length(b.IS_ENABLE),b.IS_ENABLE,'1') = '1' and b.STATUS = '1' and b.ALLOW_USE_END_DATE >= curdate() and NOT (b.ALLOW_USE_END_DATE < #{activity.allowUseBeginDate,jdbcType=DATE} OR b.ALLOW_USE_BEGIN_DATE > #{activity.allowUseEndDate,jdbcType=DATE}) ) AND PRODUCT_ID IN
#{product.productId}

 SELECT * FROM TB_ACTIVITY_REF_PRODUCT a
WHERE if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'
AND a.ACTIVITY_ID=#{activityId,jdbcType=VARCHAR}

select PRODUCT_ID as productId,SKU_ID as skuId from TB_ACTIVITY_REF_PRODUCT b where if(length(b.IS_ENABLE), b.IS_ENABLE, '1') = '1'
AND b.ACTIVITY_ID!=#{activity.uuid,jdbcType=VARCHAR}
AND EXISTS(SELECT 1 FROM TB_ACTIVITY_PROFIT a WHERE b.ACTIVITY_ID = a.UUID and if(length(a.IS_ENABLE), a.IS_ENABLE, '1') = '1' and a.STATUS = '1' and a.ALLOW_USE_END_DATE >= curdate()
and NOT (a.ALLOW_USE_END_DATE < #{activity.allowUseBeginDate,jdbcType=DATE} OR a.ALLOW_USE_BEGIN_DATE > #{activity.allowUseEndDate,jdbcType=DATE})
and if(length(a.APPLY_PRODUCT_FLAG), a.APPLY_PRODUCT_FLAG, '0') = '0') and PRODUCT_ID IN
#{product.productId}
