mapper.select:
SELECT * FROM
TB_CLIENT_COUPON a
WHERE if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'
and a.CLIENT_ID=#{clientId,jdbcType=VARCHAR}
and a.COUPON_ID=#{couponId,jdbcType=VARCHAR}
order by COUPON_AMOUT desc,VALID_END_DATE

SELECT * FROM
TB_CLIENT_COUPON a
WHERE if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'

and a.UUID=#{id,jdbcType=VARCHAR}


and a.CLIENT_ID=#{clientId,jdbcType=VARCHAR}


and a.COUPON_ID=#{couponId,jdbcType=VARCHAR}


and a.STAGE_ID=#{stageId,jdbcType=VARCHAR}

order by COUPON_AMOUT desc,VALID_END_DATE

SELECT ifnull(sum(ifnull(a.COUPON_AMOUT, 0)), 0) as CLIENT_AMOUNT,
count(a.UUID) as CLIENT_COUNT,
ifnull(sum(case when a.CREATE_TIME>CURDATE() then ifnull(a.COUPON_AMOUT, 0) else 0 end ),0) as CLIENT_PERDATE_AMOUNT,
ifnull(sum(case when a.CREATE_TIME>CURDATE() then 1 else 0 end ),0) as CLIENT_PERDATE_COUNT
FROM TB_CLIENT_COUPON a
WHERE if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'

    and a.UUID=#{id,jdbcType=VARCHAR}


    and a.CLIENT_ID=#{clientId,jdbcType=VARCHAR}


    and a.COUPON_ID=#{couponId,jdbcType=VARCHAR}


    and a.STAGE_ID=#{stageId,jdbcType=VARCHAR}

SELECT * FROM TB_CLIENT_COUPON a WHERE if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1' AND a.COUPON_STRATEGY_TYPE='0' AND a.COUPON_TYPE='1' and a.CLIENT_ID=#{clientId,jdbcType=VARCHAR}
and a.COUPON_LEVEL=#{couponLevel,jdbcType=VARCHAR}
and a.status='0' and a.VALID_START_DATE <=curdate() and a.VALID_END_DATE >= curdate() order by COUPON_AMOUT ,VALID_END_DATE

SELECT * FROM TB_CLIENT_COUPON a WHERE if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1' AND a.COUPON_STRATEGY_TYPE !='0' AND a.COUPON_TYPE='1' and a.CLIENT_ID=#{clientId,jdbcType=VARCHAR}
and a.COUPON_LEVEL=#{couponLevel,jdbcType=VARCHAR}
and a.status='0' and a.VALID_START_DATE <=curdate() and a.VALID_END_DATE >= curdate() order by COUPON_AMOUT desc,VALID_END_DATE

SELECT * FROM TB_CLIENT_COUPON a WHERE if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1' AND a.COUPON_STRATEGY_TYPE ='3' AND a.COUPON_TYPE ='3' and a.CLIENT_ID=#{clientId,jdbcType=VARCHAR}
and a.COUPON_LEVEL=#{couponLevel,jdbcType=VARCHAR}
and a.status='0' and a.VALID_START_DATE <=curdate() and a.VALID_END_DATE >= curdate() order by VALID_END_DATE

SELECT * FROM TB_CLIENT_COUPON a WHERE if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1' AND a.COUPON_STRATEGY_TYPE in ('1','2') AND a.COUPON_TYPE ='2' and a.CLIENT_ID=#{clientId,jdbcType=VARCHAR}
and a.COUPON_LEVEL=#{couponLevel,jdbcType=VARCHAR}
and a.status='0' and a.VALID_START_DATE <=curdate() and a.VALID_END_DATE >= curdate() order by COUPON_AMOUT desc,VALID_END_DATE

 SELECT * FROM
TB_CLIENT_COUPON a
WHERE if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'
AND a.uuid IN

   #{item.uuid}

and a.CLIENT_ID= #{clientId,jdbcType=VARCHAR}

mapper.update:
UPDATE TB_CLIENT_COUPON a
SET a.STATUS='0',
a.REMARK=CONCAT(ifnull(a.REMARK,''),sysdate(),'恢复券状态|'),
a.UPDATE_AUTHOR=#{orderinfo.operator,jdbcType=VARCHAR}
WHERE if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'
AND a.JOIN_ORDER_ID=#{orderinfo.orderId,jdbcType=VARCHAR}

    AND a.CLIENT_ID=#{orderinfo.clientId,jdbcType=VARCHAR}

mapper.select:
SELECT * FROM
TB_CLIENT_COUPON a
WHERE if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1'
and a.CLIENT_ID=#{clientId,jdbcType=VARCHAR}
and a.status='0'
and a.VALID_START_DATE <=curdate()
and a.VALID_END_DATE >= curdate()

    and( a.APPLY_PRODUCT_FLAG='1' or
    exists(select 1 from TB_COUPON_REF_PRODUCT b
    where b.COUPON_ID = a.UUID
    and b.PRODUCT_ID =#{productId,jdbcType=VARCHAR}

        and b.SKU_ID =#{skuId,jdbcType=VARCHAR}

    )
    )

order by COUPON_AMOUT desc,VALID_END_DATE
