mapper.select:
select COUPON_ISSUE_ID couponIssueId, COUPON_ID couponId, COUPON_NAME couponName, TARGET_TYPE targetType, IS_VISIBLE isVisible, ISSUE_TIME issueTime, ISSUE_STATUS issueStatus, CREATE_AUTHOR createAuthor, UPDATE_AUTHOR updateAuthor from TB_COUPON_ISSUE
(COUPON_ISSUE_ID = #{searchCondition,jdbcType=VARCHAR} or COUPON_NAME = #{searchCondition,jdbcType=VARCHAR} or COUPON_ID = #{searchCondition,jdbcType=VARCHAR})
and TARGET_TYPE = #{targetType,jdbcType=VARCHAR}
and IS_VISIBLE = #{isVisible,jdbcType=VARCHAR}
and ISSUE_STATUS = #{issueStatus,jdbcType=VARCHAR}
and LOGIC_DELETE = '0'

SELECT COUPON_ISSUE_ID couponIssueId, COUPON_ID couponId, COUPON_NAME couponName, TARGET_TYPE targetType, ISSUE_IDS issueIds, IS_VISIBLE isVisible, ISSUE_TIME issueTime, ISSUE_STATUS issueStatus FROM TB_COUPON_ISSUE
WHERE TARGET_TYPE = #{targetType,jdbcType=VARCHAR}
AND IS_VISIBLE = #{isVisible,jdbcType=VARCHAR} AND ISSUE_STATUS = #{issueStatus,jdbcType=VARCHAR}
AND DATE_FORMAT(ISSUE_TIME ,'%Y-%m-%d %H:%i') <= DATE_FORMAT(#{dateLine,jdbcType=DATE},'%Y-%m-%d %H:%i') AND LOGIC_DELETE=0
