mapper.update:
UPDATE TB_PRODUCT_COUPON a
SET IS_ENABLE='0',a.UPDATE_AUTHOR= #{operator,jdbcType=VARCHAR}
WHERE ifnull(a.IS_ENABLE, '1') = '1'
AND a.UUID IN

    #{baseCoupon.couponId}

mapper.select:
select CONCAT(PRODUCT_ID,'|',if(length(SKU_ID),SKU_ID,'EMPTY')) as gatherItem, count(*) as gatherValue from TB_PRODUCT_COUPON a INNER JOIN TB_COUPON_REF_PRODUCT b ON b.COUPON_ID=a.UUID and if(length(b.IS_ENABLE),b.IS_ENABLE,'1') = '1' where if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1' and a.STATUS='1' AND a.COUPON_TYPE IN ('0','1') and a.ALLOW_USE_END_DATE >= curdate() and if(length(a.APPLY_PRODUCT_FLAG),a.APPLY_PRODUCT_FLAG,'0') = '0' and PRODUCT_ID IN
#{product.productId}
group by CONCAT(PRODUCT_ID,'|',if(length(SKU_ID),SKU_ID,'EMPTY'))

select *
from TB_PRODUCT_COUPON a
where ifnull(a.IS_ENABLE, '1') = '1'

    and if(length(a.GET_TYPE),a.GET_TYPE,'1') = #{obtainType,jdbcType=VARCHAR}


    and a.STATUS=#{status,jdbcType=VARCHAR}


    and a.ALLOW_USE_BEGIN_DATE >curdate()


    and a.ALLOW_USE_BEGIN_DATE <=curdate()
    and a.ALLOW_USE_END_DATE >=curdate()


    and a.ALLOW_USE_END_DATE <curdate()


    and a.ALLOW_USE_END_DATE >curdate()


    and( a.APPLY_PRODUCT_FLAG='1' or
    exists(select 1 from TB_COUPON_REF_PRODUCT b
    where b.COUPON_ID = a.UUID
    and b.PRODUCT_ID =#{productId,jdbcType=VARCHAR}

        and b.SKU_ID =#{skuId,jdbcType=VARCHAR}

    )
    )


    and instr(#{couponType,jdbcType=VARCHAR},COUPON_TYPE) >0

Order by a.CREATE_TIME desc

select * from TB_PRODUCT_COUPON a left join TB_COUPON_QUOTA_RULE b on a.UUID = b.COUPON_ID and IFNULL(b.IS_ENABLE, '1') = '1' where if(length(a.IS_ENABLE),a.IS_ENABLE,'1') = '1' and if(length(a.GET_TYPE),a.GET_TYPE,'1') = '1'
and a.STATUS=#{status,jdbcType=VARCHAR}
and a.ALLOW_USE_BEGIN_DATE >curdate()
and a.ALLOW_USE_BEGIN_DATE <=curdate() and a.ALLOW_USE_END_DATE >=curdate()
and a.ALLOW_USE_END_DATE <curdate()
/*改动*/
and a.ALLOW_USE_END_DATE >=curdate()
and( a.APPLY_PRODUCT_FLAG='1' or exists(select 1 from TB_COUPON_REF_PRODUCT b where b.COUPON_ID = a.UUID and b.PRODUCT_ID =#{productId,jdbcType=VARCHAR}
and b.SKU_ID =#{skuId,jdbcType=VARCHAR}
) )
and instr(#{couponType,jdbcType=VARCHAR},COUPON_TYPE) >0
and (ifnull(b.PER_DATE_MAX_AMOUNT,999999999)>=999999999 or b.PER_DATE_MAX_AMOUNT > (select ifnull(sum(ifnull(d.COUPON_AMOUT, 0)),0) from TB_CLIENT_COUPON d where ifnull(d.IS_ENABLE, '1') = '1' and d.COUPON_ID = a.UUID and (d.STATUS in ('1', '2') or curdate() <= d.VALID_END_DATE) and d.CREATE_TIME >= curdate())) and (ifnull(b.MAX_AMOUNT,999999999)>=999999999 or b.MAX_AMOUNT > (select ifnull(sum(ifnull(d.COUPON_AMOUT, 0)),0) from TB_CLIENT_COUPON d where ifnull(d.IS_ENABLE, '1') = '1' and d.COUPON_ID = a.UUID and (d.STATUS in ('1', '2') or curdate() <= d.VALID_END_DATE))) and (ifnull(b.MAX_COUNT,999999999)>=999999999 or b.MAX_COUNT > (select ifnull(count(ifnull(d.UUID, 0)),0) from TB_CLIENT_COUPON d where ifnull(d.IS_ENABLE, '1') = '1' and d.COUPON_ID = a.UUID and (d.STATUS in ('1', '2') or curdate() <= d.VALID_END_DATE))) Order by a.CREATE_TIME desc
